TS=$(date -u +%Y-%m-%dT%H:%M:%SZ); LOG=$HOME/tk_global_bank/audit/global_deploy.log; PROJECT_URL=https://tkghd.vercel.app; echo "${TS},AUDIT,DEPLOY,STAGE=INIT,ENV=PROD,STATUS=START" >> $LOG; git add -A && git commit -m "ALL_MODULES_GALACTIC_FINAL_DEPLOY_${TS}" || true; git tag -f galactic-final-${TS} || true; git push origin HEAD --tags || true; vercel --prod --confirm --yes --scope tkglobal --env HUD_MODE=GALACTIC --env BASE_URL=${PROJECT_URL} --env AUDIT_LOG_PATH=$HOME/tk_global_bank/audit --env DEPLOY_TS=${TS} > /tmp/vercel_deploy.out 2>&1; STATUS=$([ $? -eq 0 ] && echo SUCCESS || echo FAIL); echo "${TS},AUDIT,DEPLOY,URL=${PROJECT_URL},ENV=PROD,STATUS=${STATUS}" >> $LOG; [ "$STATUS" = "SUCCESS" ] && echo "${TS},HUD,DEPLOY_BADGE,URL=${PROJECT_URL},ENV=PROD,STATUS=VISIBLE" >> $LOG; exit 0
